import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import Buttons from "./Buttons";
import DisplayProgress from "./DisplayProgress"

function Pomodoro() {
  //Initial timer settings
  const initialTimerSettings = {
  focusTime: 25, 
  breakTime: 5,
  focusSeconds: 1500,
  breakSeconds: 300, 
  breakTime: false, 
  isTimerRunning: false,
  currentlyActive: false
  }

  const [timerSettings, setTimerSettings] = useState({...initialTimerSettings, });

  //Initialize the initial timer settings
  const {
    focusTime,
    breakTime,
    focusSeconds,
    breakSeconds,
    isTimerRunning
  } = timerSettings;

  useInterval(
    () => {
    const timerRunning = !breakTime ? focusSeconds: breakSeconds;
    //Checks to see if the timer has reached 0, resets the timer, and plays audio for breakTime
    if(timerRunning === 0){
      setTimerSettings({
        ...timerSettings, 
        focusSeconds: focusTime * 60,
        breakSeconds: breakTime * 60,
        breakTime: !breakTime
      });
      new Audio(`https://bigsoundbank.com/UPLOAD/mp3/1482.mp3`).play();
    }

    //Counts down the timer in seconds by 1 for focus time
    else if (timerRunning > 0 && !breakTime){
      setTimerSettings({
        ...timerSettings, 
        focusSeconds: focusSeconds - 1,
      })
    }
    //Counts down the timer in seconds by 1 for break time
    else if(timerRunning > 0 && breakTime){
      setTimerSettings({
        ...timerSettings, 
        breakSeconds: breakSeconds - 1
      })
    }
    },
    isTimerRunning ? 1000 : null

  );


  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: 25:00
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: 05:00
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
